    };
  },

  computed: { ...mapGetters(["organizations", "positions"])
  },

  mounted() {
    this.getOrganizations();
    this.getPositions();
  },

  watch: {
    employeeSelected: function (value) {
      this.employee = value;
    },
    isHide: function (value) {
      if (!value && this.saveMode == 1) {
        this.getNewEmployeeCode();
        this.employee.Gender = 1;
      } else if (!value && this.saveMode == 3) {
        this.getNewEmployeeCode();
      }

      this.errorText = [], this.isShowError = [], this.isSuccess = false;
    },
    saveMode: function (value) {
      this.addMode = value;
    }
  },
  methods: { ...mapMutations(["setOrganizations", "setPositions"]),
    ...mapActions(["getOrganizations", "getPositions"]),

    /**
     * Khi click tắt form thông tin chi tiết, hiện popup info
     * Author: VQBao - 12/10/2022
     */
    hideDialogDetail() {
      this.showInfoPopup = true;
    },

    /**
     * Khi chọn không ở popup info, tắt form thông tin chi tiết
     * Author: VQBao - 12/10/2022
     */
    cancelSaveAction() {
      this.showInfoPopup = false;
      this.$emit("hideDetail", null, true);
    },

    /**
     * Khi chọn có ở popup info, tắt popup rồi thực hiện cất
     * Author: VQBao - 12/10/2022
     */
    confirmSaveAction() {
      this.showInfoPopup = false;
      this.saveOnClick();
    },

    /**
     * focus vào ô input đầu tiên mỗi khi mở form chi tiết
     * Author: VQBao - 12/10/2022
     */
    onInitialized: function (e) {
      this.elementFocus = e;
      setTimeout(function () {
        e.component.focus();
      }, 0);
    },

    /**
     * Khi bấm cất, thêm nhân viên rồi tắt form
     * Author: VQBao - 12/10/2022
     */
    async saveOnClick() {
      await this.addEmployee();

      if (this.isSuccess) {
        this.$emit("hideDetail", null, true);
      }
    },

    /**
     * Gọi api thêm, sửa nhân viên
     * Author: VQBao - 12/10/2022
     */
    async addEmployee() {
      var axiosMethod = "";
      var toastText = "";
      document.getElementById("loader").style.display = "block";
      var me = this;
      var newGuid = this.$uuid.v4();

      if (this.addMode == 1) {
        me.employee.EmployeeID = newGuid;
        axiosMethod = 'post';
        toastText = MISAResource.TOAST.Add;
      } else if (this.addMode == 2) {
        axiosMethod = 'put';
        toastText = MISAResource.TOAST.Edit;
      } else if (this.addMode == 3) {
        me.employee.EmployeeID = newGuid;
        axiosMethod = 'post';
        toastText = MISAResource.TOAST.Duplicate;
      }

      var list = [];
      list.push(this.employee);
      me.validateEmployee();

      if (this.isValid) {
        try {
          await axios({
            method: axiosMethod,
            url: ConstApi.EmployeeApi,
            data: list
          }).then(() => {
            document.getElementById("loader").style.display = "none";
            me.isSuccess = true;
            me.employee = {};
            me.$emit("loadData", 20, 1, "");
            me.$emit("showToast", toastText);
            me.getNewEmployeeCode();
            this.addMode = 1;
          }).catch(error => {
            document.getElementById("loader").style.display = "none";

            if (error.response.data.errorMsg[0] == "Mã nhân viên đã tồn tại trong hệ thống") {
              let el = document.getElementById("employeeCodeInput");
              el.classList.add("check-border");
              me.errorText[0] = MISAResource.ERROR.DuplicatedCode;
              this.showErrorPopup = true;
            }
          });
        } catch (error) {
          document.getElementById("loader").style.display = "none";
        }
      } else {
        document.getElementById("loader").style.display = "none";
      }
    },

    /**
     * Khi bấm cất và thêm, thực hiên cất rồi reset form để tiếp tục thêm mới
     * Author: VQBao - 12/10/2022
     */
    async refillFormOnClick() {
      await this.addEmployee();
      this.isSuccess = false;
      this.onInitialized(this.elementFocus);
    },

    /**
     * Check các check box là khách hàng và là nhà cung cấp
     * Author: VQBao - 12/10/2022
     */
    checkCheckbox(check) {
      if (check == 1) {
        if (this.employee.IsCustomer == 1) {
          this.employee.IsCustomer = 0;
        } else {
          this.employee.IsCustomer = 1;
        }
      } else if (check == 2) {
        if (this.employee.IsGroup == 1) {
          this.employee.IsGroup = 0;
        } else {
          this.employee.IsGroup = 1;
        }
      }
    },

    /**
     * Check radio giới tính
     * 1: Nam, 2: Nữ
     * Author: VQBao - 12/10/2022
     */
    checkRadio(check) {
      if (check == 1) {
        this.employee.Gender = 1;
      } else if (check == 2) {
        this.employee.Gender = 2;
      }
    },

    /**
     * Thực hiện validate nhân viên khi bấm cất
     * Author: VQBao - 12/10/2022
     */
    validateEmployee() {
      this.isValid = true;
      this.validateEmployeeCode();
      this.validateFullName();
      this.validateOrganization();
      this.validateDateOfBirth();
      this.validateDateOfIssuanceCIC();
      this.validateEmail();

      for (let i = 0; i < this.errorText.length; i++) {
        if (this.errorText[i]) {
          this.isValid = false;
          break;
        }
      }

      if (!this.isValid) {
        this.showErrorPopup = true;
      }
    },

    /**
     * Validate mã nhân viên
     * Author: VQBao - 12/10/2022
     */
    validateEmployeeCode() {
      let el = document.getElementById("employeeCodeInput");

      if (!this.employee.EmployeeCode) {
        el.classList.add("check-border");
        this.errorText[0] = MISAResource.ERROR.EmptyCode;
      } else {
        el.classList.remove("check-border");
        this.errorText[0] = "";
      }
    },

    /**
     * Validate tên nhân viên
     * Author: VQBao - 12/10/2022
     */
    validateFullName() {
      let el = document.getElementById("fullNameInput");

      if (!this.employee.FullName) {
        el.classList.add("check-border");
        this.errorText[1] = MISAResource.ERROR.EmptyFullName;
      } else {
        el.classList.remove("check-border");
        this.errorText[1] = "";
      }
    },

    /**
     * Validate tên đơn vị
     * Author: VQBao - 12/10/2022
     */
    validateOrganization() {
      let el = document.getElementById("organizationInput");

      if (!this.employee.OrganizationID) {
        el.classList.add("check-border");
        this.errorText[2] = MISAResource.ERROR.EmptyOrganization;
      } else {
        el.classList.remove("check-border");
        this.errorText[2] = "";
      }
    },

    /**
     * Validate email đúng định dạng
     * Author: VQBao - 12/10/2022
     */
    validateEmail() {
      let el = document.getElementById("emailInput");

      if (this.employee.Email) {
        var emailFormat = /^([a-zA-Z0-9_\-.]+)@([a-zA-Z0-9_\-.]+)\.([a-zA-Z]{2,5})$/;

        if (!emailFormat.test(this.employee.Email)) {
          el.classList.add("check-border");
          this.errorText[5] = MISAResource.ERROR.ValidEmail;
        } else {
          el.classList.remove("check-border");
          this.errorText[5] = "";
        }
      } else {
        el.classList.remove("check-border");
        this.errorText[5] = "";
      }
    },

    /**
     * Validate ngày sinh lớn hơn ngày hiện tại
     * Author: VQBao - 12/10/2022
     */
    validateDateOfBirth() {
      let el = document.getElementById("dobInput");

      if (this.employee.DateOfBirth) {
        let today = new Date();
        today = this.convertDate(today);
        let dob = this.employee.DateOfBirth;
        dob = this.convertDate(dob); // dob = new Date(dob);

        console.log(today, dob);

        if (dob > today) {
          console.log("ok");
          el.classList.add("check-border");
          this.errorText[3] = MISAResource.ERROR.ValidDateOfBirth;
        } else {
          el.classList.remove("check-border");
          this.errorText[3] = "";
        }
      } else {
        el.classList.remove("check-border");
        this.errorText[3] = "";
      }
    },

    /**
     * Validate ngày cấp không lớn hơn ngày hiện tại
     * Author: VQBao - 12/10/2022
     */
    validateDateOfIssuanceCIC() {
      let el = document.getElementById("doiInput");

      if (this.employee.DateOfIssuanceCIC) {
        let today = new Date();

        if (this.employee.DateOfIssuanceCIC > today) {
          el.classList.add("check-border");
          this.errorText[4] = MISAResource.ERROR.ValidDateOfIssuanceCIC;
        } else {
          el.classList.remove("check-border");
          this.errorText[4] = "";
        }
      } else {
        el.classList.remove("check-border");
        this.errorText[4] = "";
      }
    },

    /**
     * Lấy mã nhân viên mới
     * Author: VQBao - 11/10/2022
     */
    getNewEmployeeCode() {
      try {
        axios.get(ConstApi.EmployeeApi + "/NewEmployeeCode").then(response => {
          if (!this.isHide) {
            this.employee.EmployeeCode = response.data;

            if (this.addMode == 1) {
              this.employee.Gender = 1;
            }
          }
        });
      } catch (error) {
        console.log(error);
      }
    },

    /**
     * Đóng popup báo lỗi
     * Author: VQBao - 11/10/2022
     */
    closeErrorPopup() {
      this.showErrorPopup = false;
      this.onInitialized(this.elementFocus);
    },

    /**
     * Đóng popup thông báo dữ liệu thay đổi
     * Author: VQBao - 11/10/2022
     */
    closeInfoPopup() {
      this.showInfoPopup = false;
      this.onInitialized(this.elementFocus);
    },

    convertDate(date) {
      let newDate = new Date(date);

      if (date && newDate instanceof Date && !isNaN(newDate.valueOf())) {
        let _day = newDate.getUTCDate();

        let _month = newDate.getUTCMonth() + 1;

        let _year = newDate.getUTCFullYear();

        newDate = new Da();
      }

      return newDate;
    }

  }
};
