import axios from 'axios'

const state = {
    employees: [],
    employeePaging: {
        totalRecords: null,
        totalPages: null,
        pageSize: null,
        pageNumber: null
    },
    employeeSelected: {},
}

const getters = {
    employees(state){
        return state.employees;
    },
    employeePaging(state){
        return state.employeePaging;
    },
    employeeSelected(state){
        return state.employeeSelected;
    }
}

const mutations = {
    /**
     * Lấy dữ liệu phân trang nhân viên
     * Author: VQBao - 11/10/2022
     */
    getEmployeePaging(state, data){
        state.employees = data.Data;
        state.employeePaging.totalRecords = data.TotalRecords;
        state.employeePaging.totalPages = data.TotalPages;
        state.employeePaging.pageSize = data.PageSize;
        state.employeePaging.pageNumber = data.PageNumber;
    },

    /**
     * Lấy dữ liệu nhân viên theo id
     * Author: VQBao - 11/10/2022
     */
    getEmployeeById(state, data){
        state.employeeSelected = data;
    }
}

const actions = {
    /**
     * Lấy dữ liệu phân trang nhân viên
     * Author: VQBao - 11/10/2022
     */
    async getEmployeePagingData({commit}, {pageSize, pageNumber, searchKey}){
        try {
            await axios.get(`https://localhost:44317/api/v1/Employees/Filter`, {
                params: {
                    pageSize: pageSize,
                    pageNumber: pageNumber,
                    searchKey: searchKey
                }
            }).then((response) => {
                commit("getEmployeePaging", response.data)
            })
        } catch (error) {
            console.log(error);
        }
    },

    /**
     * Lấy dữ liệu nhân viên theo id
     * Author: VQBao - 11/10/2022
     */
     async getEmployeeDataById({commit}, id){
        try {
            await axios.get(`https://localhost:44317/api/v1/Employees`, {params: {id: id}})
            .then((response) => {
                commit("getEmployeeById", response.data)
            })
        } catch (error) {
            console.log(error);
        }
    },
}

export default {
    state,
    getters,
    mutations,
    actions
}
